import Hapi from '@hapi/hapi';
const start = async () => {
    const server = Hapi.server({
        port: 8000, 
        host: 'localhost',
    });

    server.route({
        method: 'GET',
        path: '/general',
        handler: (req, h) => {
            return 'Hello from /general';
        }
    })

    server.route({
        method: 'GET', 
        path: '/general/specific',
        handler: (req, h) => {
            return "Hello from /general/specific"
        }
    })

    await server.start();
    console.log(`Server is listening on ${server.info.uri}`);


}

process.on('unhandledRejection', err => {
    console.log(err);
    process.exit(1);
});

start();
